using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using HelloWorldMVC.Models;
using Microsoft.AspNetCore.Mvc;

namespace HelloWorldMVC.Controllers
{
    
    public class ProductController : Controller
    {
        static List<Product> productList = new List<Product>()
        {
            new Product(){Id=1,Name="Astrazeneca",Price= 555.7M,IsOnSale=true,PictureUrl="data:image/jpeg;base64,"},
            new Product(){Id=2,Name="Pfizer",Price=655.65M,IsOnSale=false,PictureUrl="data:image/jpeg;base64,"},
            new Product(){Id=3,Name="Moderna",Price=125.33M,IsOnSale=true,PictureUrl="data:image/jpeg;base64,"},
        };

        public IActionResult Details(int id)
        {
            var p = productList.SingleOrDefault(p => p.Id == id);
            return View(p);
        }

        public IActionResult Update(Product product_From_input)
        {
            //find the product from the lists
            var productToUpdate = productList.SingleOrDefault(p => p.Id == product_From_input.Id);
            //update it with values from user's input
            productToUpdate.IsOnSale = product_From_input.IsOnSale;
            productToUpdate.Name = product_From_input.Name;
            productToUpdate.Price = product_From_input.Price;
            productToUpdate.PictureUrl = product_From_input.PictureUrl;
            //view everything
            return RedirectToAction("ViewAll");

        }

        public IActionResult Edit(int id)
        {
            var p = productList.SingleOrDefault(p => p.Id == id);
            return View(p);
        }

        public IActionResult Create()
        {
            return View();
        }

        public IActionResult Delete(int id)
        {
            var p = productList.SingleOrDefault(p => p.Id == id);
            productList.Remove(p);
            return RedirectToAction("ViewAll");
        }

        public IActionResult Search(string txtSearch)
        {
            if (txtSearch==null)
            {
                return RedirectToAction("ViewAll");
            }
            var result = productList.Where(p => p.Name.Contains(txtSearch)).ToList();
            return View("ViewAll", result);
        }
        public IActionResult DoCreate(Product product)
        {
            bool isValid = true;


            if (product.Name == null || product.Name.Length < 3)
            {
                isValid = false;
                ModelState.AddModelError("Name", "Sorrry!!! Name property must >2 characters!");
            }
            //if(product.Price < 10)
            //{
            //    isValid = false;
            //    ModelState.AddModelError("Price", "Sorrry!!! Price property must > 10");
            //}

            if (isValid==false)
            {
                //something wrong, go back to change the data
                return View("Create");
            }
            else
            {
                //otherwise add this into database then display everything
                productList.Add(product);
                return RedirectToAction("ViewAll");
            }           
        }
        public IActionResult ViewAll()
        {
            //show the view with this list(model)
            return View(productList);
        }

        public IActionResult Index()
        {
            return View();
        }
    }
}
